export const UserLanding = ({ navigation }) => {
    const { t, i18n } = useTranslation();
    const {user, } = useUser()
    const [trayIsOpen, setTrayIsOpen] = useState(false)


    const menu = (
        <View style={styles.slideMenu}>
            <Pressable android_ripple={{color:"lightgrey", borderless:false}} onPress={()=>navigation.navigate("UserFavorites")}>
                <Text>{t("user.side_panel_favorites")}</Text>
            </Pressable>
            <Pressable android_ripple={{color:"lightgrey", borderless:false}} onPress={()=>navigation.navigate("UserProfile")}>
                <Text>{t("user.side_panel_profile")}</Text>
            </Pressable>
        </View>
    )



    const renderCategory = ({item}) => {
        return (
            <View style={styles.category}>
                <Pressable onPress={()=> navigation.navigate("UserSearch", {"category":item.name})}
                style={{flex:1}}>
                    <View style={styles.categoryImageContainer}>
                        <Image style={styles.categoryImage} source={{uri: item.photo}}></Image>
                    </View>
                    <Text style={{textAlign:"center"}}>{item.name}</Text>
                </Pressable>
            </View>
        )
    }



    useEffect(() => {
        navigation.setOptions({
            headerLeft:() => (
                <View style={styles.rippleFix}>
                    <Pressable onPress={()=>setTrayIsOpen(true)} android_ripple={{color:"lightgrey", borderless:false}}>
                        <Text style={styles.headerText}>👤</Text>
                    </Pressable>
                </View>
            ),
            headerRight:() => (
                <View style={styles.rippleFix}>
                    <Pressable onPress={()=>setTrayIsOpen(true)} android_ripple={{color:"lightgrey", borderless:false}}>
                        <Text style={styles.headerText}>📍Añadir ubicación</Text>
                    </Pressable>
                </View>
            ),
            headerTitle: "",
        })
    }, [navigation])

    return (
        <SideMenu menu={menu} isOpen={trayIsOpen} overlayColor={"#70706fAA"}
                  bounceBackOnOverdraw={false} style={styles.slideMenu} onChange={e=>setTrayIsOpen(e)}>
            <View style={styles.container}>
                <View style={styles.firstContainer}>
                    <Text style={styles.welcomeText}>Hola {user.name}!</Text>
                    <View style={styles.searchContainer}>
                        <TextInput placeholder={t("user.search_bar_placeholder")}
                                   style={[styles.yellow, styles.searchField]} onTouchStart={()=>navigation.navigate("UserSearch")}/>
                        <View style={[styles.rippleFix, styles.yellow, styles.filterButtonContainer]}>
                            <Pressable android_ripple={{color:"lightgrey", borderless:false}} style={styles.filterButton}
                                       onPress={()=>navigation.navigate("UserSearchFilters")}>
                                <Text>{t("user.filters")}</Text>
                            </Pressable>
                        </View>
                    </View>
                </View>
                <View style={styles.categoryListContainer}>
                    <Text style={styles.listTitleText}>{t("user.title_categories")}</Text>
                    <FlatList data={dataCategorias} renderItem={renderCategory} horizontal style={styles.categoryList}/>
                </View>
                <View style={styles.restaurantListContainer}>
                    <Text style={styles.listTitleText}>{t("user.title_restaurants")}</Text>
                    <RestaurantList navigation={navigation} restaurantList={dataRestaurantes} />
                </View>
            </View>
        </SideMenu>
    )
}

